import { useEffect, useLayoutEffect, useState } from "react";
useLayoutEffect


export const Count = ()=>{
    const [count, setCount] = useState(0)

    useEffect(() => {
      // 
      if(count > 3) setCount(0)
    }, [count]);

    const handleRun = ()=>{
        setCount(count + 1)
    }
    return(

        <div>
            <h3>{count}</h3>
            <button onClick={handleRun}>Run</button>
        </div>
    )
}
