import { useState } from "react"


const order = [100,200,300]

function App(){
  const [count, setCount] = useState(()=>{
    const totalPrice = order.reduce((totalOrder, totalCurrent) => totalOrder + totalCurrent)
    return totalPrice
  })

  //tạo bộ đếm
  const handleCount = () =>{
    //setCount(count + 1)
    //callback
    setCount(prevCounter => prevCounter + 1 )
  }

  return(
    <div className="App" style={{margin: 50}}>
      <h2>{count}</h2>
      <button onClick={handleCount}>+</button>
    </div>
  )
}

export default App
