import { useState } from "react";




function App() {

  const [counter, setCounter] = useState(0)
  const handleIncrease = () =>{
    for(let i = 0; i < setCounter.length; i++){
      if(setCounter[i] === 0){
        counter++
      }
    }
  }
  return (
    <div className="App" style={{padding: 30}}>
      <h1>{counter}</h1>
      <button onClick={handleIncrease}>+</button>
    </div>
  );
}

export default App;
