import { Context } from "./Context"
import './App.css'
//Context: 




export const UseConTex = ()=>{
    const [theme, setTheme] = useState('dark')

    const toggleTheme = () =>{
        setTheme(theme === 'dark' ?'light' : 'dark')
    }
    return(
        <div>
            <button onClick={toggleTheme}>Toggle Button</button>
            <Context theme={theme}/>
        </div>
    )
}
