import { useState } from "react"



function App(){
  const [count, setCount] = useState(0)

  //tạo bộ đếm
  const handleCount = () =>{
    //setCount(count + 1)
    //callback
    setCount(prevCounter => prevCounter )
  }
  return(
    <div className="App" style={{margin: 50}}>

      <h2>{count}</h2>
      <button onClick={handleCount}>+</button>
    </div>
  )
}

export default App
