//useReducer
import { useReducer, useRef } from "react";

const title = {
  color: "red",
  fontSize: "20px",
};

const span = {
    cursor: 'pointer' 
}

const styleDiv = {
    padding: '0 20px'
}
//1.inti state
const initState = {
  job: "", //giá tri khởi tạo job
  jobs: [], // giá tri hiển thi list todo list
};

//3. reducer
const reducer = (state, action) => {
  let newState;
  switch (action.type) {
    case SET_JOB:
      newState = {
        ...state,
        job: action.payload,
      };
      break;

    case ADD_JOB:
      newState = {
        ...state,
        jobs: [...state.jobs, action.payload],
      };
      break;

    case DELETE_JOB:
      const newJob = [...state.jobs];
      newJob.splice(action.payload, 1);
      newState = {
        ...state,
        jobs: newJob,
      };
      break;
    default:
      throw new Error("Invalid action");
  }
  return newState;
};



//4.dispatch
function TodoApp() {
  //
  const [state, dispatch] = useReducer(reducer, initState);
  //destructuring 
  const { job, jobs } = state;
  const inputRef = useRef();

    //
  const handleSubmit = () => {
    dispatch(addJob(job));
    dispatch(setJob(""));
    inputRef.current.focus();
  };

  return (
    <div style={styleDiv}>
      <h3 style={title}>Todo List</h3>
      <input
        ref={inputRef}
        value={job}
        placeholder="Enter todo..."
        onChange={(e) => {
          dispatch(setJob(e.target.value));
        }}
      />
      <button onClick={handleSubmit}>Add</button>
      <ul>
        {jobs.map((job, index) => (
          <li key={index}>
            {job}
            <span style={span} onClick={() => dispatch(deleteJob(index))}>&times;</span>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default TodoApp
